module IngredientsHelper

  #generates options array from the ingredients hash for <select> tags in forms
  #pass it a symbol for the ingredient type (either :malts, :hops, or :yeasts)
  def options_generator(ingredient_type)
    return if ingredients[ingredient_type] == nil

    options = []

    ingredients[ingredient_type].each do |key, value|

      if(ingredient_type == :yeasts)
        display_text = value[:number] + ' ' + value[:name]
      else
        display_text = value[:name]
      end

      options.push([display_text, key.to_s])
    end

    return options
  end

  #returns a hash of the selected ingredient
  def get_ingredient(ingredient_type, ingredient_id)
    return ingredients[ingredient_type.to_sym][ingredient_id.to_sym]
  end

